/**
 * This component allows to edit a Rental
 */
@Component({
    selector: 'app-rental-edit',
    templateUrl: 'rental-edit.component.html',
    styleUrls: ['rental-edit.component.css']
})
export class RentalEditComponent implements OnInit {
    item: Rental;
    model: Rental;
    formValid: Boolean;

    constructor(
    private rentalService: RentalService,
    private route: ActivatedRoute,
    private location: Location) {
        // Init item
        this.item = new Rental();
    }

    /**
     * Init
     */
    ngOnInit() {
        this.route.params.subscribe(param => {
            const id: string = param['id'];
            if (id !== 'new') {
                this.rentalService.get(id).subscribe(item => this.item = item);
            }
            // Get relations
        });
    }


    /**
     * Save Rental
     *
     * @param {boolean} formValid Form validity check
     * @param Rental item Rental to save
     */
    save(formValid: boolean, item: Rental): void {
        this.formValid = formValid;
        if (formValid) {
            if (item._id) {
                this.rentalService.update(item).subscribe(data => this.goBack());
            } else {
                this.rentalService.create(item).subscribe(data => this.goBack());
            } 
        }
    }

    /**
     * Go Back
     */
    goBack(): void {
        this.location.back();
    }


}



