/**
 * This component shows a list of Rental
 * @class RentalListComponent
 */
@Component({
    selector: 'app-rental-list',
    templateUrl: './rental-list.component.html',
    styleUrls: ['./rental-list.component.css']
})
export class RentalListComponent implements OnInit {
    list: Rental[];
    search: any = {};
    idSelected: string;
    constructor(
        private rentalService: RentalService,
    ) { }

    /**
     * Init
     */
    ngOnInit(): void {
        this.rentalService.list().subscribe(list => this.list = list);
    }

    /**
     * Select Rental to remove
     *
     * @param {string} id Id of the Rental to remove
     */
    selectId(id: string) {
        this.idSelected = id;
    }

    /**
     * Remove selected Rental
     */
    deleteItem() {
        this.rentalService.remove(this.idSelected).subscribe(data => this.list = this.list.filter(el => el._id !== this.idSelected));
    }

}
